const hasOverallScore = /\*\*\s*\d+\/100\s*\*\*/.test(output);
const hasCategoryScores = /\|\s*\w+.*\|\s*\d+\/100\s*\|/.test(output);
const hasScoreBreakdown = /Score Breakdown/i.test(output);
const hasWeightedScores = /Weight/.test(output);

