  .filter(Boolean).length / 4;

return {
  pass: score >= 0.75,
  score: score,
  reason: `Scoring markers: ${Math.round(score * 100)}% (Overall: ${hasOverallScore}, Categories: ${hasCategoryScores}, Breakdown: ${hasScoreBreakdown}, Weights: ${hasWeightedScores})`
};

